import groovy.json.JsonSlurper

// get properties from testCase
def testCaseProperty = testRunner.testCase.getPropertyValue( "id" ).replace( "Thank you for the feedback. It was assigned an ID ", "" )
testRunner.testCase.setPropertyValue( "id", testCaseProperty )

import groovy.lang.Binding;
import groovy.util.GroovyScriptEngine;
import com.eviware.soapui.support.GroovyUtils;

def groovyUtils = new GroovyUtils(context);

// define location of script file relative to project file
String projectPath = "";
if (System.properties['os.name'].toLowerCase().contains('windows')) {
  projectPath = groovyUtils.projectPath + "\\groovy";
} else {
  projectPath = "./groovy";
}

GroovyScriptEngine gse = new GroovyScriptEngine(projectPath);	// Groovy Script Engine runs Script
externalScript = gse.loadScriptByName("script.groovy");		// Load the Groovy Script file 
instance = externalScript.newInstance();					// Create a runtime instance of script
assert instance != null;

def testCase = testRunner.testCase;

//instance.runTestStep(testCase, "testRESTfulApi");
def json = instance.getJsonResponseByStep(testCase, "testRESTfulApi");

assert json.id==9999;
assert json.name=="Sergey Myasnikov";
assert json.email=="sergey@myasnikov@russia.ru";
assert json.feedback=="This is a feedback service helthcheck";
assert json.isSpamAgreed==true;

