if (System.properties['os.name'].toLowerCase().contains('windows')) {
  projectPath = groovyUtils.projectPath + "\\groovy";
} else {
  projectPath = "./groovy";
}

GroovyScriptEngine gse = new GroovyScriptEngine(projectPath);	// Groovy Script Engine runs Script
externalScript = gse.loadScriptByName("script.groovy");		// Load the Groovy Script file 
